$(document).ready(function() {

    $("input.mod_quiz-next-nav[value='Next page']").on("focus", function() {
        var r = confirm("Are you sure you want to go to the NEXT PAGE?\n• Click [OK] to go to the next page\n• Click [Cancel] to stay on this page");
        if (r == true) {
            $("input.mod_quiz-next-nav").blur()
            $("input.mod_quiz-next-nav").click()
        } else {
            $("input.mod_quiz-next-nav").blur()
        };
    });

    $("input.mod_quiz-next-nav[value='Finish attempt ...']").on("focus", function() {
        var r = confirm("Are you sure you want to FINISH THE ATTEMPT?\n• Click [OK] to finish the attempt\n• Click [Cancel] to stay on this page");
        if (r == true) {
            $("input.mod_quiz-next-nav").blur()
            $("input.mod_quiz-next-nav").click()
        } else {
            $("input.mod_quiz-next-nav").blur()
        };
    });

});

/**
Limit the number of options that can be selected in a 
multiple choice to 2. 
**/
var checkboxes = document.querySelectorAll('input[type="checkbox"][name*="choice"]');
debugger;
var counter = 0;

init(checkboxes);

function init(checkboxes){
  checkboxes.forEach(function(box) {
    if(box.checked){
      counter++;
    }
  });
}
function limitSelections(checkbox) {
  debugger;
  checkbox.addEventListener('click', (e) => {
	if(counter > 1){
      if(checkbox.checked == false){
        counter--;
      }

      checkbox.checked = false;
      
    }else{
      if(checkbox.checked == false){
        counter --;
      }else {
      	counter++;
      }
    }
    
  });
}

checkboxes.forEach(limitSelections);

/**
Change quiz buttons Next,Previous and Check from input type to
button type and add font-awesome icons (checkmarks and arrows).
Add some CSS to change the appearance of other parts of the quiz.
**/


var check = document.getElementsByClassName('submit');
if(check.length > 0){
	check = check[0];
	var icon= '<i class="icon fa fa-check-circle-o" aria-hidden="true"></i> '+check.value;
	check.outerHTML = check.outerHTML.replace(/\<input/, '<button class="pretty" ' ) + icon+ '</button>';
}

var previous = document.querySelectorAll('[name="previous"]');
if(previous.length > 0){
	previous = previous[0];
	var icon= '<i class="icon fa fa-arrow-left" aria-hidden="true"></i> '+previous.value;
	previous.outerHTML = previous.outerHTML.replace(/^\<input/, '<button class="pretty"') + icon+ '</button>';
}
var next = document.querySelectorAll('[name="next"]');
if(next.length > 0){
	next = next[0];
	var icon= '<i class="icon fa fa-arrow-right primary" aria-hidden="true"></i> '+next.value;
	next.outerHTML = next.outerHTML.replace(/^\<input/, '<button class ="pretty mod_quiz-next-nav" ') + icon+ '</button>';
}

